% Zwave_Mahajan is a Matlab function that generates a matrix of the wavefront aberration for the % set of coefficients.function waveabermap = CompWaveOSA(PARAMS,c);waveabermap = Zwave_MahajanOSA(PARAMS,c);waveabermap=transpose(waveabermap);n = size(waveabermap);% Define the axes of the wavefront plot in cyc/degreeaxispupil = -PARAMS.PupilSize/2:PARAMS.PupilSize/n(1):(PARAMS.PupilSize/2)-(PARAMS.PupilSize/n(1));% set parameters and display the wavefront aberration as a contour plotv=floor(min(min(waveabermap)))-0.25:0.25:ceil(max(max(waveabermap)))+0.25;scrsz = get(0,'ScreenSize');figure('Position',[0 500 400 400]); %rect = [left, bottom, width, height]contourf(axispupil, axispupil,waveabermap,v);colormap('default');colorbar;title('Wavefront Aberration');xlabel('mm (right-left)');ylabel('mm (superior-inferior)');axis square;zlabel('microns');